#' se_bd <- sqrt(diag(vcov_bd))
#' ci_beta_bd <- mle[2] + c(-1, 1) * 1.96 * se_bd[1]
#' ci_delta_bd <- mle[4] + c(-1, 1) * 1.96 * se_bd[2]
#' 
#' # Plot selected ellipses side by side
#' par(mfrow = c(1, 3), mar = c(4, 4, 3, 1))
